INSERT INTO `Articles` (`Id`, `Id_Users`, `Category`, `Category_unaccent`, `Title`, `Title_unaccent`, `Content`, `Registered`, `Edited`, `Type`) VALUES
(1, 1, 'Framework', 'framework', 'Diel 2 - trvalé prihlásenie', 'diel-2-trvale-prihlasenie', '<h3 style="text-align:justify">Perzistentné prihlásenie</h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Logika&nbsp;trváleho prihlásenia (bez nutnosti viacnásobnej autentifikácii) je popísaná v nasledujúcich krokoch.</span></span></p>\r\n\r\n<ul>\r\n	<li style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">vytvorenie tokena</span></span></li>\r\n	<li style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">prepojenie na úložiska</span></span></li>\r\n	<li style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">overenie pri volaní stránky</span></span></li>\r\n</ul>\r\n\r\n<h3 style="text-align:justify"><span style="font-family:trebuchet ms,helvetica,sans-serif"><span style="font-size:14px">Vytvorenie tokena</span></span></h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Pri prvom úspešnom prihlásení užívateľa sa vytvorí token prostredníctvom ktorého je možné vyvolávať stránku bez nutnosti autentifikácie. Token je vytvorený&nbsp;spojením troch globálnych premenných (ip adresa,&nbsp;akceptovaná hlavička,&nbsp;agent):</span></span></p>\r\n\r\n<div style="text-align:justify;background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><strong>$_SERVER[''</strong><em>REMOTE_ADDR</em><strong>'']<br />\r\n<span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><strong>$_SERVER[''</strong></span></span></strong><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><em>HTTP_ACCEPT</em></span></span><strong><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><strong>'']<br />\r\n<span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><strong>$_SERVER[''</strong></span></span></strong></span></span></strong><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><em>HTTP_USER_AGENT</em></span></span></span></span><strong><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><strong><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><strong>'']</strong></span></span></strong></span></span></strong></span></span></div>\r\n\r\n<p style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">ktoré sú spojené prostredníctvom delimetra ":" do reťazca.&nbsp;Premenná<strong>&nbsp;$_SERVER[''</strong><em>HTTP_USER_AGENT</em><strong>'']&nbsp;</strong>je upravená do prehľadnejšieho tvaru - rozklad položiek do poľa a spojenie do reťazca.&nbsp;</span></span></p>\r\n\r\n<h3 style="text-align:justify"><span style="font-size:14px">Prepojenie na úložiska</span></h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Vytvorený token je uložený do databázy a do premennej $_COOKIE na lokálnom disku. Zároveň sa k vytvorenému tokenu&nbsp;ukladá do databázy vygenerované platné session id, ktoré sa taktiež uloží aj do&nbsp;$_COOKIE.</span></span></p>\r\n\r\n<h3 style="text-align:justify"><span style="font-family:trebuchet ms,helvetica,sans-serif"><span style="font-size:14px">Overenie pri volaní stránky</span></span></h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Prvotné overenie je na základe&nbsp;porovnania&nbsp;ešte raz&nbsp;vygenerovaného&nbsp;tokenu podľa rovnakých kritérii ako token ukladaný do databázy a $_COOKIE&nbsp;(ip adresa, agent, akceptovaná hlavička)&nbsp;s&nbsp;existujúcim&nbsp;tokenom&nbsp;uloženým v $_COOKIE. Ak súhlasia prechádza sa na overenie existencie tokenu v databáze na základe session_id uloženej v druhej&nbsp;$_COOKIE.&nbsp;Ak je v databáze nájdený záznam, overí sa prislúchajúci token ešte raz s tokenom uloženým v&nbsp;$_COOKIE.&nbsp;&nbsp;</span></span></p>\r\n', '2015-07-26 11:45:30', NULL, 'released'),
(3, 2, 'Framework', 'framework', 'Diel 3 - Práca s premennými', 'diel-3-praca-s-premennymi', '<p style="text-align: justify;"><span style="font-size:14px"><span style="font-family:trebuchet ms,helvetica,sans-serif">V pohľade s&uacute; premenn&eacute; volan&eacute; prostredn&iacute;ctvom syntax: </span></span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:14px"><span style="font-family:trebuchet ms,helvetica,sans-serif"><strong>$this-&gt;meno_premennej </strong></span></span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:14px"><span style="font-family:trebuchet ms,helvetica,sans-serif">V kontrol&eacute;ry je premenn&aacute; uložen&aacute; v poli s premenn&yacute;mi <strong>$variables</strong> v tvare: </span></span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:14px"><span style="font-family:trebuchet ms,helvetica,sans-serif"><strong>$this-&gt;variables[&#39;</strong><em>meno_premennej</em><strong>&#39;] </strong>= <em>&#39;obsah_premennej</em>&#39;</span></span></p>\r\n', '2015-07-27 12:35:01', NULL, 'draft'),
(6, 1, 'Atmega8', 'atmega8', 'Diel 1 - schémy', 'diel-1-schemy', '<h4>ATmega8l-8PU</h4>\r\n\r\n<h4>Konfigurácia pinov</h4>\r\n\r\n<p style="text-align:center"><br />\r\n<strong><span style="font-family:arial,helvetica,sans-serif"><span style="font-size:10px">Obr 1 - Konfigurácia pinov ATmega8(L)</span></span></strong></p>\r\n\r\n<h4>Konfigurácia ISP konektora</h4>\r\n\r\n<p style="text-align:center"><a href="/Public/images/atmel/atmega8/ISPheader.gif" target="_blank"><img alt="" src="/Public/images/atmel/atmega8/ISPheader.gif" style="height:148px; line-height:20.8px; width:150px" /></a><br />\r\n<strong><span style="font-family:arial,helvetica,sans-serif"><span style="font-size:10px">Obr 2&nbsp;- Konfigurácia pinov ISP konektora (10 pin)</span></span></strong></p>\r\n', '2016-02-17 20:35:32', NULL, 'draft'),
(13, 1, 'Framework', 'framework', 'Diel 1 - úprava reťazca', 'diel-1-uprava-retazca', '<h3>Konverzia reťazca</h3>\r\n\r\n<p style="text-align: justify;"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">Na pr&aacute;cu s url adresou a datab&aacute;zou je potrebn&aacute; transform&aacute;cia &uacute;hľadn&yacute;ch url adries. Jedn&aacute; z možnost&iacute; ako upravovať n&aacute;zov (titulok) pozn&aacute;mky do &uacute;hľadnej url adresy je uveden&yacute; niž&scaron;ie.</span></span></p>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="color:rgb(38, 38, 38); font-family:droid sans,arial,helvetica,sans-serif; font-size:12px"><span style="font-family:courier new,courier,monospace"><strong>public function</strong>&nbsp;unAccentUrl(<span style="color:rgb(0, 0, 255)">$string</span>)<br />\r\n{<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(128, 0, 0)">// Trim empty characters</span><br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$string</span>&nbsp;= trim(<span style="color:rgb(0, 0, 255)">$string</span>);<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(128, 0, 0)">/***<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* &Aacute;: Amp Aacute;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* &Agrave;: Amp Agrave;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* &Acirc;: Amp Acirc;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* &Atilde;: Amp Atilde;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* &Auml;: Amp Auml;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* &Aring;: Amp Aring;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* &AElig;: Amp AElig;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* &Ccedil;: Amp Ccedil;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* &Oslash;: Amp Oslash;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* Č: Amp Ccaron;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* Ĳ: Amp IJlig;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* Ŀ: Amp Lmidot;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;* Ī: Amp Imacr;</span></span></span><br />\r\n<span style="color:rgb(38, 38, 38); font-family:droid sans,arial,helvetica,sans-serif; font-size:12px"><span style="font-family:courier new,courier,monospace"><span style="color:rgb(128, 0, 0)">&nbsp; &nbsp; &nbsp; &nbsp;*/&nbsp;</span>&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;&nbsp;=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;acute|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;grave|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;cedil|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;slash|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;caron|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;tilde|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;midot|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;circ|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name&nbsp;</span>.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;macr|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;ring|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;uml|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;lig|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_nam</span>e .=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;orn|&quot;</span>;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$utf8_name</span>&nbsp;.=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;th&quot;</span>;<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(128, 0, 0)">// Convert string to htm entities</span>&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$string</span>&nbsp;= htmlentities(<span style="color:rgb(0, 0, 255)">$string</span>, ENT_HTML5 | ENT_QUOTES,&nbsp;<span style="color:rgb(0, 100, 0)">&quot;UTF-8&quot;</span>);<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(128, 0, 0)">// Find html entites defined by &amp;[a-zA-Z]$utf8_name;<br />\r\n&nbsp; &nbsp; &nbsp; // example see above $utf8_name</span><br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$pattern</span>&nbsp;=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;/&amp;([a-z]{1,2})(?:&quot;</span>.<span style="color:rgb(0, 0, 255)">$utf8_name</span>.<span style="color:rgb(0, 100, 0)">&quot;);/i&quot;</span>;<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(128, 0, 0)">// Replace html entities by given char<br />\r\n&nbsp; &nbsp; &nbsp; // note: array of replacement chars is placed in first item of found array</span><br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$converted</span>&nbsp;= strtolower(preg_replace(<span style="color:rgb(0, 0, 255)">$pattern</span>,&nbsp;<span style="color:rgb(0, 0, 255)">$replacement</span>&nbsp;=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;$1&quot;</span>,&nbsp;<span style="color:rgb(0, 0, 255)">$string</span>));<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(128, 0, 0)">// Replaced the rest untranslited characters</span><br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$replaced</span>&nbsp;= preg_replace(<span style="color:rgb(0, 0, 255)">$pattern</span>&nbsp;=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;/[^a-z0-9]/&quot;</span>,&nbsp;<span style="color:rgb(0, 0, 255)">$replacement</span>&nbsp;=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;-&quot;</span>,&nbsp;<span style="color:rgb(0, 0, 255)">$converted</span>);<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(128, 0, 0)">// Replaced multiple &#39;-&#39; characters</span><br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">$clean_url</span>&nbsp;= preg_replace(<span style="color:rgb(0, 0, 255)">$pattern</span>&nbsp;=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;/[-]+/&quot;</span>,&nbsp;<span style="color:rgb(0, 0, 255)">$replacement</span>&nbsp;=&nbsp;<span style="color:rgb(0, 100, 0)">&quot;-&quot;</span>,&nbsp;<span style="color:rgb(0, 0, 255)">$replaced</span>);<br />\r\n<br />\r\n&nbsp; &nbsp; &nbsp; return&nbsp;<span style="color:rgb(0, 0, 255)">$clean_url</span>;<br />\r\n}</span></span></div>\r\n', '2016-02-21 12:25:55', NULL, 'draft'),
(9, 1, 'Atmega8', 'atmega8', 'Diel 2 - príklad s LED', 'diel-2-praca-s-led', '<h3>Rozblikanie led diódy</h3>\r\n\r\n<h4><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Program na rozblikanie LED diódy (pin 0&nbsp;na porte C)</span></span></h4>\r\n\r\n<p><span style="color:rgb(75, 0, 130)"><span style="font-size:12px"><span style="font-family:courier new,courier,monospace">#define F_CPU 8000000UL</span></span><br />\r\n<br />\r\n<span style="font-size:12px"><span style="font-family:courier new,courier,monospace">#include "avr/io.h"<br />\r\n#include "util/delay.h"</span></span></span><br />\r\n<br />\r\n<span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><span style="color:rgb(0, 100, 0)">int&nbsp;</span>main(<span style="color:rgb(0, 128, 0)">void</span>)<br />\r\n{<br />\r\n&nbsp;&nbsp;</span></span><span style="color:rgb(0, 0, 255)"><span style="font-size:12px"><span style="font-family:courier new,courier,monospace">// nastavenie pinu PC0</span></span></span><br />\r\n<span style="font-size:12px"><span style="font-family:courier new,courier,monospace">&nbsp; DDRC |=&nbsp;<span style="color:rgb(178, 34, 34)">0x00000001</span>;<br />\r\n&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">// nekonecny cyklus</span><br />\r\n&nbsp;&nbsp;<strong>while</strong>(<span style="color:rgb(165, 42, 42)">1</span>) {<br />\r\n&nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">// nastavenie pinu PC0 na log 1</span><br />\r\n&nbsp; &nbsp; PORTC |= (<span style="color:rgb(165, 42, 42)">1</span>&nbsp;&lt;&lt; PC0);<br />\r\n&nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">// zasatavenie na 1 s</span><br />\r\n&nbsp; &nbsp; _delay_ms(<span style="color:rgb(165, 42, 42)">1000</span>);<br />\r\n&nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">// nastavenie pinu PC0 na log 0</span><br />\r\n&nbsp; &nbsp; PORTC &amp;= ~(<span style="color:rgb(165, 42, 42)">1</span>&nbsp;&lt;&lt; PC0) ;<br />\r\n&nbsp; &nbsp;&nbsp;<span style="color:rgb(0, 0, 255)">// zastavenie na 1 s</span><br />\r\n&nbsp; &nbsp; _delay_ms(<span style="color:rgb(165, 42, 42)">1000</span>);<br />\r\n&nbsp; }</span></span><br />\r\n​<span style="font-size:12px"><span style="font-family:courier new,courier,monospace">&nbsp;&nbsp;<strong>return</strong>&nbsp;<span style="color:rgb(165, 42, 42)">0</span>;<br />\r\n}</span></span></p>\r\n\r\n<h4>Preklad a nahratie programu</h4>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:courier new,courier,monospace">avr-gcc -Wall -Os -mmcu=atmega8 -c -o main.o main.c<br />\r\navr-gcc -Wall -mmcu=atmega8 main.o -o main<br />\r\navr-objcopy -O ihex -R .eeprom main main.hex<br />\r\navrdude -P /dev/ttyACM0 -b 19200 -c avrisp -p m8 -u -U flash:w:main.hex:i</span></span></p>\r\n\r\n<h4>Odporúčané odkazy</h4>\r\n\r\n<p><em><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><a href="http://treehouseprojects.ca/fusebits/" target="_blank">http://treehouseprojects.ca/fusebits/</a></span></span></em></p>\r\n', '2016-02-17 20:38:23', NULL, 'released'),
(14, 1, 'Atmega8', 'atmega8', 'Diel 3 - príklad s LCD', 'diel-3-praca-s-lcd', '<h3>Komunikácia s LCD 16x2 znakov</h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>Upozornenie!&nbsp;</strong><span style="color:rgb(38, 38, 38)">Pri komunikácii s displejom je potrebné v hlavičkovom súbore&nbsp;</span><strong>lcd.h</strong><span style="color:rgb(38, 38, 38)">&nbsp;nastaviť správne&nbsp;</span><u><strong><span style="color:rgb(255, 0, 0)">port</span></strong></u><span style="color:rgb(38, 38, 38)">&nbsp;spolu s&nbsp;</span><strong><span style="color:rgb(255, 0, 0)"><u>pinmi</u>&nbsp;</span></strong><span style="color:rgb(38, 38, 38)">a na začiatku v&nbsp;súbore&nbsp;</span><strong>lcd.c</strong><span style="color:rgb(38, 38, 38)">&nbsp;definovať správnu frekvenciu taktovania mikrokontroléra (ATmega8L-8PU =&gt; 8Mhz #define F_CPU 8000000UL).&nbsp;</span></span></span></p>\r\n\r\n<h4 style="text-align:justify">Použité knižnice</h4>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><a href="/Public/file/Atmega8/lcd/lcd.h" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no,width=800,left=100,height=600,top=100''); return false;">lcd.h</a><br />\r\n<a href="/Public/file/Atmega8/lcd/lcd.c" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no,width=800,left=100,height=600,top=100''); return false;">lcd.c</a></span></span></p>\r\n\r\n<h4 style="text-align:justify">Program</h4>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><span style="color:rgb(128, 0, 128)">#define F_CPU 8000000UL</span><br />\r\n<span style="color:rgb(128, 0, 128)">#include "util/delay.h"<br />\r\n#include "stdio.h"<br />\r\n#include "lcd.h"<br />\r\n<br />\r\n#define SECOND 1000</span><br />\r\n<br />\r\n<span style="color:rgb(0, 0, 255)">// seconds delay</span><br />\r\n<span style="color:rgb(0, 128, 0)">void</span><span style="color:rgb(38, 38, 38)">&nbsp;delay (</span><span style="color:rgb(0, 128, 0)">uint8_t</span><span style="color:rgb(38, 38, 38)">&nbsp;N);</span><br />\r\n<span style="color:rgb(0, 0, 255)">// display text with specific time</span><br />\r\n<span style="color:rgb(0, 128, 0)">void</span><span style="color:rgb(38, 38, 38)">&nbsp;display (</span><span style="color:rgb(0, 128, 0)">char</span><span style="color:rgb(38, 38, 38)">&nbsp;*text,&nbsp;</span><span style="color:rgb(0, 128, 0)">uint8_t</span><span style="color:rgb(38, 38, 38)">&nbsp;seconds);</span><br />\r\n<br />\r\n<span style="color:rgb(0, 0, 255)">// main function</span><br />\r\n<span style="color:rgb(0, 128, 0)">int</span><span style="color:rgb(38, 38, 38)">&nbsp;main (</span><span style="color:rgb(0, 128, 0)">void</span><span style="color:rgb(38, 38, 38)">)</span><br />\r\n<span style="color:rgb(38, 38, 38)">{</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp;&nbsp;</span><span style="color:rgb(0, 128, 0)">char</span><span style="color:rgb(38, 38, 38)">&nbsp;buffer[2];</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp;&nbsp;</span><span style="color:rgb(0, 128, 0)">int</span><span style="color:rgb(38, 38, 38)">&nbsp;i = 9;</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp;</span><span style="color:rgb(0, 0, 255)">&nbsp;// initialize display, cursor off</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; lcd_init(LCD_DISP_ON);</span><br />\r\n<span style="color:rgb(0, 0, 255)">&nbsp; // decounting</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp;&nbsp;</span><strong>while</strong><span style="color:rgb(38, 38, 38)">&nbsp;(i&gt;=0) {</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp;&nbsp;</span><span style="color:rgb(0, 0, 255)">// go to position</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp; lcd_gotoxy(7, 0);</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp;&nbsp;</span><span style="color:rgb(0, 0, 255)">// int to string</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp; sprintf(buffer, "0%d", i);</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp;</span><span style="color:rgb(0, 0, 255)">&nbsp;// display string</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp; display(buffer, 1);</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp;</span><span style="color:rgb(0, 0, 255)">&nbsp;// decrement</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp; i--;</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; }</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp;&nbsp;</span><span style="color:rgb(0, 0, 255)">// blink the text&nbsp;</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp;&nbsp;</span><strong>while</strong><span style="color:rgb(38, 38, 38)">&nbsp;(1) {</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp;&nbsp;</span><span style="color:rgb(0, 0, 255)">// go to position</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp; lcd_gotoxy(6, 0);</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp;</span><span style="color:rgb(0, 0, 255)">&nbsp;// display BOOM</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp; display("BOOM", 1);</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp;&nbsp;</span><span style="color:rgb(0, 0, 255)">// clear display and home cursor</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp; lcd_clrscr();</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp;</span><span style="color:rgb(0, 0, 255)">&nbsp;// display nothing</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp; display("", 1);&nbsp;</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; }</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp;&nbsp;</span><span style="color:rgb(0, 0, 255)">// navrat</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp;&nbsp;</span><strong>return</strong><span style="color:rgb(38, 38, 38)">&nbsp;0;</span><br />\r\n<span style="color:rgb(38, 38, 38)">}</span><br />\r\n<span style="color:rgb(0, 0, 255)">/***<br />\r\n&nbsp;* Show/display text on screen with specific delay<br />\r\n&nbsp;*<br />\r\n&nbsp;* @param char * - text<br />\r\n&nbsp;* @param uint8_t - number of seconds<br />\r\n&nbsp;* @return void<br />\r\n&nbsp;*/</span><br />\r\n<span style="color:rgb(0, 128, 0)">void</span><span style="color:rgb(38, 38, 38)">&nbsp;display (</span><span style="color:rgb(0, 128, 0)">char</span><span style="color:rgb(38, 38, 38)">&nbsp;*text,</span><span style="color:rgb(0, 128, 0)">&nbsp;uint8_t</span><span style="color:rgb(38, 38, 38)">&nbsp;seconds)&nbsp;</span><br />\r\n<span style="color:rgb(38, 38, 38)">{</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp;&nbsp;</span><span style="color:rgb(0, 0, 255)">// string</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; lcd_puts(text);</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp;&nbsp;</span><span style="color:rgb(0, 0, 255)">// delay</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; delay(seconds);</span><br />\r\n<span style="color:rgb(38, 38, 38)">}</span><br />\r\n<span style="color:rgb(0, 0, 255)">/***<br />\r\n&nbsp;* Delay in seconds<br />\r\n&nbsp;*<br />\r\n&nbsp;* @param uint8_t - number of seconds<br />\r\n&nbsp;* @return void<br />\r\n&nbsp;*/</span><br />\r\n<span style="color:rgb(0, 128, 0)">void&nbsp;</span><span style="color:rgb(38, 38, 38)">delay(</span><span style="color:rgb(0, 128, 0)">uint8_t</span><span style="color:rgb(38, 38, 38)">&nbsp;N)</span><br />\r\n<span style="color:rgb(38, 38, 38)">{</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp;&nbsp;</span><strong>while</strong><span style="color:rgb(38, 38, 38)">&nbsp;(N)</span><span style="color:rgb(38, 38, 38)">&nbsp;{</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp; _delay_ms(SECOND);</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; &nbsp; N--;</span><br />\r\n<span style="color:rgb(38, 38, 38)">&nbsp; }</span><br />\r\n<span style="color:rgb(38, 38, 38)">}</span></span></span></p>\r\n\r\n<h4>Preklad a nahratie programu</h4>\r\n\r\n<p><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">avr-gcc -Wall -Os -mmcu=atmega8 -c lcd.c&nbsp;main.c</span></span><br />\r\n<span style="font-family:courier new,courier,monospace"><span style="font-size:12px">avr-gcc -Wall -mmcu=atmega8 lcd.o main.o -o main</span></span><br />\r\n<span style="font-family:courier new,courier,monospace"><span style="font-size:12px">avr-objcopy -O ihex -R .eeprom main main.hex</span></span><br />\r\n<span style="font-size:12px"><span style="font-family:courier new,courier,monospace">avrdude -P /dev/ttyACM0 -c usbasp&nbsp;-p m8 -u -U flash:w:main.hex:i</span></span></p>\r\n\r\n<h4>Ukážka</h4>\r\n\r\n<p><a href="/Public/images/atmel/atmega8/lcd-2.png" target="_blank"><img alt="" src="/Public/images/atmel/atmega8/lcd-2.png" style="height:420px; width:600px" /></a></p>\r\n\r\n<h4>Odporúčané odkazy</h4>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Knižnica -</span></span>&nbsp;<a href="http://homepage.hispeed.ch/peterfleury/avr-software.html" target="_blank"><em><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">http://homepage.hispeed.ch/peterfleury/avr-software.html</span></span></em></a><br />\r\n<span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Postup -&nbsp;</span><span style="font-family:arial,helvetica,sans-serif"><em><a href="http://homepage.hispeed.ch/peterfleury/doxygen/avr-gcc-libraries/group__pfleury__lcd.html" target="_blank">http://homepage.hispeed.ch/peterfleury/doxygen/avr-gcc-libraries/group__pfleury__lcd.html</a></em></span></span></p>\r\n', '2016-02-23 20:42:39', NULL, 'released'),
(15, 1, 'CKEditor', 'ckeditor', 'Diel 1 - upload obrázkov', 'diel-1-upload-obrazkov', '<h3>Pr&aacute;ca s obr&aacute;zkami&nbsp;v CKEditore</h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">D&ocirc;ležitou častou, ktor&uacute; je potrebn&eacute; v CKEditore doplniť, je tlač&iacute;tko s možnosťou uploadovania s&uacute;borov na server (obr&aacute;zkov, postupov, n&aacute;vodov v .pdf, .tex, ... ). V tomto čl&aacute;nku uv&aacute;dzam stručny n&aacute;vod ako postupovať pri doplnen&iacute; tejto funkcionality, ktor&aacute; V&aacute;m umožn&iacute; jednoduch&eacute; prehľad&aacute;vanie s&uacute;borov v priečinkovej &scaron;trukt&uacute;re a n&aacute;sledn&eacute; uploadovanie s&uacute;borov na str&aacute;nku. Podkladom obsahu čl&aacute;nku je tento&nbsp;<a class="dokument" href="http://www.mixedwaves.com/2010/02/integrating-fckeditor-filemanager-in-ckeditor/" title="Intagration FCKEditor filemanager in CKEditor">zdroj</a></span>.</span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">V prvom kroku je potrebn&eacute; stiahnuť CKEditor z ofici&aacute;lnej&nbsp;<a class="dokument" href="http://ckeditor.com/download" title="CKEditor download">str&aacute;nky</a>&nbsp;a integrovať do projektu, napr.</span></span></p>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:11px"><span style="font-family:courier new,courier,monospace">Download&nbsp;<strong>CKEditor</strong><br />\r\nSkop&iacute;ruj&nbsp;<strong>CKEditor</strong>&nbsp;do</span><strong>&nbsp;/<span style="font-family:courier new,courier,monospace">cesta_k_Vasmu_projektu/filemanager</span></strong></span></div>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Ďalej je potrebn&eacute; stiahnuť zabalen&yacute;&nbsp;<a class="dokument" href="http://www.mixedwaves.com/filemanager_in_ckeditor.zip" title="Zip - zdroj">priečinok</a>&nbsp;z adresy zdroja. Po rozbalen&iacute; je d&ocirc;ležit&yacute; iba priečinok filemanager (<span style="font-family:courier new,courier,monospace">../js/ckeditor/filemanager</span>), ktor&yacute; treba nakop&iacute;roval do CKEditoru v projekte</span></span></p>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:11px"><span style="font-family:courier new,courier,monospace">Skop&iacute;ruj&nbsp;<strong>/downloads/filemanager_in_ckeditor/js/ckeditor/filemanager/</strong><br />\r\ndo&nbsp;<strong>/var/www/composer/chat/library/filemanager/</strong></span></span></div>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Prepojenie vytvoren&eacute;ho textov&eacute;ho editora&nbsp;</span></span></p>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:11px"><span style="font-family:courier new,courier,monospace">textarea name =&nbsp;&#39;Content&#39; rows=&#39;10&#39; cols=&#39;50&#39; id=&#39;id-editor&#39;</span></span></div>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">s CKEditorom je možn&eacute; viacer&yacute;mi možnosťami. Jednotl&iacute;ve alternat&iacute;vy s&uacute; pop&iacute;san&eacute; v online&nbsp;<a class="dokument" href="http://docs.ckeditor.com/#!/guide/dev_configuration" title="Dokumentácia CKEditor">dokument&aacute;cii</a>&nbsp;CKEditoru. V tomto pr&iacute;pade je CKEditor prepojen&yacute; s textov&yacute;m poľom n&aacute;sledovn&yacute;m sp&ocirc;sobom</span></span></p>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:11px"><span style="font-family:courier new,courier,monospace">CKEDITOR.replace( &#39;id-editor&#39;, {</span><br />\r\n<span style="font-family:courier new,courier,monospace">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong>language</strong>: &#39;sk&#39;,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong>uiColor</strong>: &#39;#dfefcf&#39;,</span><br />\r\n<span style="font-family:courier new,courier,monospace">} );</span></span></div>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">CKEditor disponuje vlastnosťou&nbsp;<span style="font-family:courier new,courier,monospace">language</span>, prostredn&iacute;ctvom ktorej&nbsp;si možeme vybrať&nbsp;jazyk.&nbsp;V&nbsp;tomto&nbsp;pr&iacute;pade bola zvolen&aacute;&nbsp;slovenčina. Druh&yacute;m parametrom je&nbsp;<strong><span style="font-family:courier new,courier,monospace">uiColor</span></strong>, ktor&yacute; predstavuje farbu CKEditoru. Dialogov&eacute; okno pri priložen&iacute; obr&aacute;zka je uveden&eacute; na Obr. 1 a)</span></span></p>\r\n\r\n<p><span style="font-family:verdana,geneva,sans-serif; font-size:14px"><a href="/Public/images/framework/bez_integracie.png" target="_blank"><img alt="CKEditor bez integrácie" id="picture" src="/Public/images/framework/bez_integracie.png" style="height:259px; margin:5px; width:236px" /></a>&nbsp;&nbsp;<a href="/Public/images/framework/s_integraciou.png" target="_blank"><img alt="CKEditor po integrácii" id="picture" src="/Public/images/framework/s_integraciou.png" style="height:260px; margin:5px; width:237px" /></a></span></p>\r\n\r\n<p><span style="font-size:10px"><strong><span style="font-family:verdana,geneva,sans-serif">Obr. 1&nbsp;Zobrazenie dialogov&eacute;ho okna s tlačidlom&nbsp;<q>Prech&aacute;dzať server</q></span>&nbsp;a)&nbsp;<span style="font-family:verdana,geneva,sans-serif">bez integr&aacute;cie, b)&nbsp;s integr&aacute;ciou</span></strong></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Ako je možn&eacute; vidieť, ch&yacute;ba tlačidlo&nbsp;<q>Prech&aacute;dzať server</q></span>.&nbsp;Roz&scaron;&iacute;renie dialogov&eacute;ho okn&aacute; o ch&yacute;baj&uacute;ce tlač&iacute;dlo je možn&eacute; volan&iacute;m parametrov v skripte CKEditoru, pridan&iacute;m za parameter farby</span></p>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:11px"><span style="font-family:courier new,courier,monospace"><code>var <strong>typFile</strong> = &#39;Type=File&#39;;<br />\r\nvar <strong>typImage</strong> = &#39;Type=Image&#39;;<br />\r\nvar <strong>okno </strong>= &#39;/library/ckeditor/filemanager/browser/default/browser.html&#39;;<br />\r\nvar <strong>upload</strong> = &#39;/library/ckeditor/filemanager/connectors/php/upload.php&#39;;<br />\r\nvar <strong>konektor</strong> = &#39;/library/ckeditor/filemanager/connectors/php/connector.php&#39;;</code><br />\r\n<br />\r\n<code>CKEDITOR.replace( &#39;id-editor&#39;, {</code><br />\r\n<code>&nbsp; &nbsp;<strong>language</strong>: &#39;sk&#39;,<br />\r\n&nbsp; &nbsp;<strong>uiColor</strong>: &#39;#dfefcf&#39;,<br />\r\n&nbsp; &nbsp;<strong>filebrowserBrowseUrl</strong>: okno + &#39;?Connector=&#39; + konektor,<br />\r\n&nbsp; &nbsp;<strong>filebrowserImageBrowseUrl</strong>: okno + &#39;?&#39; + typImage + &#39;&amp;Connector=&#39; + konektor,<br />\r\n&nbsp; &nbsp;<strong>filebrowserUploadUrl</strong>: upload + &#39;?&#39; + typFile,<br />\r\n&nbsp; &nbsp;<strong>filebrowserImageUploadUrl</strong>: upload + &#39;?&#39; + typImage</code><br />\r\n<code>} );</code></span></span></div>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Parameter&nbsp;<span style="font-family:courier new,courier,monospace">filebrowserBrowserUrl</span>&nbsp;definuje zobrazenie tlač&iacute;dla&nbsp;</span><q><span style="font-family:verdana,geneva,sans-serif">Prech&aacute;dzať server</span></q><span style="font-family:verdana,geneva,sans-serif">. Na Obr.1 b) je zobrazen&eacute; dialogov&eacute; okno s pridan&yacute;m tlačidlom. Nato aby bolo tlačidlo funkčn&eacute;, t.j. aby po otvoren&iacute; dialogov&eacute;ho okn&aacute; sa zobrazila ponuka (adres&aacute;r)&nbsp;s obr&aacute;zkami, je d&ocirc;ležit&eacute; urobiť v&nbsp;s&uacute;bore&nbsp;<strong>config.php<span style="color:rgb(139, 69, 19)">&nbsp;</span></strong>(v adres&aacute;ry<strong>&nbsp;.../filemanager/connectors/php/</strong>)&nbsp;</span><span style="font-family:verdana,geneva,sans-serif">nasleduj&uacute;ce zmeny</span></span></p>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:11px"><span style="font-family:courier new,courier,monospace"><strong>$Config</strong>[&#39;UserFilesPath&#39;]&nbsp;= adresar_v_ktorom_sa_nachadza_adresar_s_obrazkami<br />\r\n<strong>$Config</strong>[&#39;UserFilesPath&#39;] = &#39;/public/&#39;</span></span></div>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Keďže v tomto pr&iacute;pade bola adres&aacute;rov&aacute; &scaron;trukt&uacute;ra tak&aacute;, že obr&aacute;zky sa nach&aacute;dzali v adres&aacute;ry&nbsp;<strong>images</strong>, bolo nutn&eacute; zmeniť e&scaron;te riadky v&nbsp;<strong>config.php</strong>.</span></span></p>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:11px"><span style="font-family:courier new,courier,monospace"><strong>$Config</strong>[&#39;FileTypesPath&#39;][&#39;Image&#39;]&nbsp;=&nbsp;<strong>$Config</strong>[&#39;UserFilesPath&#39;] . &#39;images/&#39; ;<br />\r\n<strong>$Config</strong>[&#39;FileTypesAbsolutePath&#39;][&#39;Image&#39;] =&nbsp;(</span><span style="font-family:courier new,courier,monospace"><strong>$Config</strong>[&#39;UserFilesAbsolutePath&#39;] == &#39;&#39;)&nbsp;</span><span style="font-family:courier new,courier,monospace">? &#39;&#39; :&nbsp;<strong>$Config</strong>[&#39;UserFilesAbsolutePath&#39;].&#39;images/&#39; ;</span></span></div>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><span style="font-family:verdana,geneva,sans-serif">A takto vyzer&aacute; dialogov&eacute; okno s nač&iacute;tan&yacute;mi obr&aacute;zkami</span></span></span></p>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:14px"><a href="/Public/images/framework/s_funkcnostou.png" target="_blank"><img alt="Dialógové okno čítajúce z nadefinovaného adresára" id="picture" src="/Public/images/framework/s_funkcnostou.png" style="height:302px; width:500px" /></a></span></span></span></span></p>\r\n\r\n<p><span style="font-size:10px"><strong><span style="font-family:verdana,geneva,sans-serif">Obr. 2 Dialogov&eacute; okno s obr&aacute;zkami</span></strong></span></p>\r\n', '2016-02-27 06:52:52', NULL, 'released'),
(16, 1, 'Ubuntu', 'ubuntu', 'Diel 1 - flashplugin installer', 'diel-1-flashplugin-installer', '<h3>Flash plugin installer crash</h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Po in&scaron;tal&aacute;cii pluginu flashplugin r&ocirc;znymi sp&ocirc;sobmi (terminal, Ubuntu Software Center, Synaptic Package manager) nebolo možn&eacute; spňu&scaron;ťať vide&aacute;, ktorňe boli&nbsp;z&aacute;visl&eacute;&nbsp;od flash pluginu. V Chrome a Chromiu sa vyskytovalo chybov&eacute; hl&aacute;senie <strong>Could not load Shockwave Flash</strong>, pričom vo Firefoxe preblik&aacute;vala plocha s videom.</span>&nbsp;&nbsp;</span> &nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Chromium Web Browser - Could not load Shockwave Flash</span></span></li>\r\n	<li><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Firefox Web Browser (Mozzila) - preblik&aacute;vanie plochy s videom</span></span></li>\r\n	<li><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Google Chrome - Could not load Shockwave Flash&nbsp;</span></span></li>\r\n</ul>\r\n\r\n<h4>Rie&scaron;enie</h4>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Podľa n&aacute;vodu bolo zisten&eacute;, že posledn&aacute; verzia Adobe flashplugin-u nie je v &uacute;plnom poriadku. Najjednoduchj&scaron;&iacute;m rie&scaron;en&iacute;m, ktor&eacute; je v tomto pr&iacute;pade aj naj&uacute;čelnej&scaron;ie je prejsť na star&scaron;iu verziu flashplugin-u.</span></span></p>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:11px"><span style="font-family:courier new,courier,monospace">libflashplayer.so =&gt; /usr/lib/flashplugin-installer/</span></span></div>\r\n\r\n<p><span style="font-size:14px"><span style="font-family:verdana,geneva,sans-serif"><strong>Odpor&uacute;čan&eacute; odkazy</strong></span></span></p>\r\n\r\n<p><em><span style="font-family:arial,helvetica,sans-serif"><span style="font-size:12px"><a href="http://helpx.adobe.com/flash-player/kb/archived-flash-player-versions.html?PID=3662453" target="_blank">http://helpx.adobe.com/flash-player/kb/archived-flash-player-versions.html?PID=3662453</a><br />\r\n<a href="http://richardappleby.wordpress.com/2012/07/03/getting-adobe-flash-to-work-on-ubuntu-12-04/" target="_blank">http://richardappleby.wordpress.com/2012/07/03/getting-adobe-flash-to-work-on-ubuntu-12-04/</a></span></span></em></p>\r\n', '2016-02-27 07:18:40', NULL, 'draft'),
(17, 1, 'Ubuntu', 'ubuntu', 'Diel 2 - plocha v Ubuntu', 'diel-2-plocha-v-ubuntu', '<h3>Aktivovanie pracovnej plochy v Ubuntu</h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Nereagovanie po kliknut&iacute; prav&yacute;m tlačidlom na plochu.</span></span></p>\r\n\r\n<h4>Rie&scaron;enie</h4>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:11px"><span style="font-family:courier new,courier,monospace">gsettings set org.gnome.desktop.background show-desktop-icons true</span></span></div>\r\n\r\n<h4>Odpor&uacute;čan&eacute; odkazy</h4>\r\n\r\n<p><span style="font-family:arial,helvetica,sans-serif"><em><span style="font-size:12px"><a href="http://askubuntu.com/questions/14092/i-cannot-right-click-on-the-desktop" target="_blank">http://askubuntu.com/questions/14092/i-cannot-right-click-on-the-desktop</a></span></em></span></p>\r\n', '2016-02-27 07:37:2', NULL, 'draft');
INSERT INTO `Articles` (`Id`, `Id_Users`, `Category`, `Category_unaccent`, `Title`, `Title_unaccent`, `Content`, `Registered`, `Edited`, `Type`) VALUES
(18, 1, 'Atmega8', 'atmega8', 'Diel 4 - príklad s PWM', 'diel-4-praca-s-pwm', '<h3>Fázovo šírkova modulácia</h3>\r\n\r\n<p style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">Na ovládanie, resp. reguláciu napätia na výstupnom tranzistore je použitá fázovo-širková modulácia. Tá umožňuje prostredníctvom vhodne navrhnutého dolnopriepustného filtra privádzať potrebné napätie na bázu výstupného tranzistora, ktorý ovláda výstupne napätie regulovateľného zdroja napätia. Knižnica&nbsp;fázovo šírkovej modulácie je uvedená nižšie.&nbsp;</span></span></p>\r\n\r\n<h4 style="text-align: justify;">Použité knižnice</h4>\r\n\r\n<p style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><a href="/Public/file/Atmega8/pwm/pwm.h" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no,width=800,left=100,height=600,top=100''); return false;">pwm.h</a><br />\r\n<a href="/Public/file/Atmega8/pwm/pwm.c" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no,width=800,left=100,height=600,top=100''); return false;">pwm.c</a></span></span></span><br />\r\n<span style="font-family:courier new,courier,monospace"><span style="font-size:12px"><a href="/Public/file/Atmega8/lcd/lcd.h" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no,width=800,left=100,height=800,top=100''); return false;">lcd.h</a><br />\r\n<a href="/Public/file/Atmega8/lcd/lcd.c" onclick="window.open(this.href, '''', ''resizable=no,status=no,location=no,toolbar=no,menubar=no,fullscreen=no,scrollbars=no,dependent=no,width=800,left=100,height=600,top=100''); return false;">lcd.c</a></span></span></p>\r\n\r\n<h4>Zdrojový kód</h4>\r\n\r\n<p><span style="font-family:courier new,courier,monospace"><span style="font-size:12px"><span style="color:#800080">#define F_CPU 8000000UL<br />\r\n#include "avr/io.h"<br />\r\n#include "avr/interrupt.h"<br />\r\n#include "util/delay.h"<br />\r\n#include "stdio.h"<br />\r\n#include "stdlib.h"<br />\r\n#include "lib/lcd.h"<br />\r\n#include "lib/pwm.h"</span><br />\r\n<br />\r\n<span style="color:#800080">#define MODE 5<br />\r\n#define PRESCALER PRESCALER_1<br />\r\n#define ADC_CHANNEL(CHANNEL) { ADMUX = 0xF0; ADMUX &amp;= CHANNEL; }</span><br />\r\n<br />\r\n<span style="color:#006400">char</span> <strong>to_lcd</strong>[16];<br />\r\n<br />\r\n<span style="color:#006400">void</span> <strong>pwm_init</strong>(<span style="color:#006400">void</span>);<br />\r\n<span style="color:#006400">void</span> <strong>int_init</strong>(<span style="color:#006400">void</span>);<br />\r\n<span style="color:#006400">void</span> <strong>adc_init</strong>(<span style="color:#006400">void</span>);<br />\r\n<span style="color:#006400">void</span> <strong>lcd_show</strong>(<span style="color:#006400">void</span>);<br />\r\n<span style="color:#006400">uint16_t </span><strong>adc_read</strong>(<span style="color:#006400">uint8_t</span> channel);<br />\r\n<br />\r\n<span style="color:#0000CD">// Pridavanie striedy</span><br />\r\nISR (INT0_vect)<br />\r\n{<br />\r\n&nbsp; if (OCR1A &lt; 251) {<br />\r\n&nbsp; &nbsp; OCR1A = OCR1A + 5;<br />\r\n&nbsp; &nbsp; lcd_show();<br />\r\n&nbsp; }<br />\r\n}<br />\r\n<span style="color:#0000CD">// Uberanie striedy</span><br />\r\nISR (INT1_vect)<br />\r\n{<br />\r\n&nbsp; if (OCR1A &gt; 4) {<br />\r\n&nbsp; &nbsp; OCR1A = OCR1A - 5;<br />\r\n&nbsp; &nbsp; lcd_show(); &nbsp;<br />\r\n&nbsp; }<br />\r\n}<br />\r\n<br />\r\n<span style="color:#0000CD">/***<br />\r\n&nbsp;* &nbsp;Hlavna funkcia<br />\r\n&nbsp;*<br />\r\n&nbsp;* @param Void<br />\r\n&nbsp;* @return Void<br />\r\n&nbsp;*/</span><br />\r\nint main (void)<br />\r\n{<br />\r\n&nbsp; <span style="color:#0000CD">// Inicializacia displeja</span><br />\r\n&nbsp; lcd_init(LCD_DISP_ON);<br />\r\n&nbsp; <span style="color:#0000CD">// spustenie pwm</span><br />\r\n&nbsp; pwm_init();<br />\r\n&nbsp; <span style="color:#0000CD">// spustenie povolenie preruseni od tlacidiel</span><br />\r\n&nbsp; int_init();<br />\r\n&nbsp; <span style="color:#0000CD">// inicializacia adc</span><br />\r\n&nbsp; adc_init();<br />\r\n&nbsp; <span style="color:#0000CD">// vymazanie obrazovky</span><br />\r\n&nbsp; lcd_clrscr();<br />\r\n&nbsp; <span style="color:#0000CD">// vypis textu</span><br />\r\n&nbsp; lcd_puts(" START WITH PUSH");<br />\r\n&nbsp; <span style="color:#0000CD">// povol globalne prerusenia&nbsp;</span><br />\r\n&nbsp; sei(); &nbsp;&nbsp;<br />\r\n<span style="color:#0000CD">&nbsp; // nekonecna slucka</span><br />\r\n&nbsp; while(1) { &nbsp;} &nbsp;<br />\r\n}<br />\r\n<span style="color:#0000FF">/***<br />\r\n&nbsp;* Inicializacia PWM&nbsp;<br />\r\n&nbsp;* - vystup PB0 (IC1A)<br />\r\n&nbsp;* - vyber modu 5, t.j. Fast PWM TOP = 255,<br />\r\n&nbsp;* - nastavenie pociatocnej striedy PWM DUTY = 110/255<br />\r\n&nbsp;*<br />\r\n&nbsp;* @param Void<br />\r\n&nbsp;* @return Void<br />\r\n&nbsp;*/</span><br />\r\nvoid pwm_init(void)<br />\r\n{<br />\r\n&nbsp; <span style="color:#0000CD">// PB0 ako vystupny (OC1A)</span><br />\r\n&nbsp; DDRB |= 0x02;<br />\r\n&nbsp; <span style="color:#0000CD">// Nastavenie pociatocnej hodnoty PWM 110/255</span><br />\r\n&nbsp; OCR1A = 110;<br />\r\n&nbsp; <span style="color:#0000CD">// Nastavenie PWM modu a preddelicky</span><br />\r\n&nbsp; pwm_select(MODE, PRESCALER);<br />\r\n&nbsp; <span style="color:#0000CD">// Mazanie pri zhode TCNT1 a OCR1A, nastavenie pri BOTTOM</span><br />\r\n&nbsp; TCCR1A |= (1 &lt;&lt; COM1A1);<br />\r\n}<br />\r\n<span style="color:#0000CD">/***<br />\r\n&nbsp;* Praca s prerusenim od tlacidiel&nbsp;<br />\r\n&nbsp;* - vstupy PD2 (INT0) a PD3 (INT1)<br />\r\n&nbsp;* - aktivacia pull-up rezistorov na vstupe<br />\r\n&nbsp;* - povolenie preruseni od vstupov<br />\r\n&nbsp;*<br />\r\n&nbsp;* @param Void<br />\r\n&nbsp;* @return Void<br />\r\n&nbsp;*/</span><br />\r\nvoid int_init(void)<br />\r\n{<br />\r\n&nbsp; <span style="color:#0000CD">// PD2 a PD3 ako vstupne piny</span><br />\r\n&nbsp; DDRD &amp;= ~((1 &lt;&lt; PD2) | (1 &lt;&lt; PD3));&nbsp;<br />\r\n&nbsp; <span style="color:#0000CD">// Aktivovanie pull-up rezistora</span><br />\r\n&nbsp; PORTD |= (1 &lt;&lt; PD2) | (1 &lt;&lt; PD3);<br />\r\n&nbsp; <span style="color:#0000CD">// Povolenie prerusenia</span><br />\r\n&nbsp; GICR |= (1 &lt;&lt; INT0) | (1 &lt;&lt; INT1); &nbsp;<br />\r\n&nbsp; <span style="color:#0000CD">// Dobezna hrana INT0 a INT1</span><br />\r\n&nbsp; MCUCR |= (1 &lt;&lt; ISC01) | (1 &lt;&lt; ISC11);<br />\r\n}<br />\r\n<span style="color:#0000CD">/***<br />\r\n&nbsp;* Analogovo digitalny prevodnik&nbsp;<br />\r\n&nbsp;* - vstup PC0 (ADC0)<br />\r\n&nbsp;* - referencne napatie AVcc s externym kondenzatorom na AREF pine<br />\r\n&nbsp;* - preddelicka 64 bity ADPS2:0 = 6 v ADCSRA, kvoli podmienke, ze&nbsp;<br />\r\n&nbsp;* &nbsp; frekvencia prevodu ma byt v rozmedzi 50-200 kHz. Pri 8Mhz a preddelicke<br />\r\n&nbsp;* &nbsp; frekvancia prevodu je 125kHz<br />\r\n&nbsp;*<br />\r\n&nbsp;* @param Void<br />\r\n&nbsp;* @return Void<br />\r\n&nbsp;*/</span><br />\r\nvoid adc_init(void)<br />\r\n{&nbsp;<br />\r\n&nbsp; <span style="color:#0000CD">// referencne napatie AVcc s externym kondenzatorom na AREF pine&nbsp;</span><br />\r\n&nbsp; ADMUX |= (1 &lt;&lt; REFS0);<br />\r\n&nbsp; <span style="color:#0000CD">// nastavenie preddelicky 64</span><br />\r\n&nbsp; ADCSRA |= (1 &lt;&lt; ADPS2) | (1 &lt;&lt; ADPS1);<br />\r\n}<br />\r\n<span style="color:#0000CD">/***<br />\r\n&nbsp;* Citanie prevodu po ukonceni<br />\r\n&nbsp;*<br />\r\n&nbsp;* @param long - vstup pre prevodnik<br />\r\n&nbsp;* @return uint16_t - hodnota prevodu<br />\r\n&nbsp;*/</span><br />\r\nuint16_t adc_read(uint8_t channel)<br />\r\n{<br />\r\n&nbsp; <span style="color:#0000CD">// vstupny kanal PC0 (ADC0)</span><br />\r\n&nbsp; ADC_CHANNEL(channel);<br />\r\n&nbsp; <span style="color:#0000CD">// spustenie prevodu</span><br />\r\n&nbsp; ADCSRA |= (1 &lt;&lt; ADSC);<br />\r\n&nbsp; <span style="color:#0000CD">// povolenie prevodu</span><br />\r\n&nbsp; ADCSRA |= (1 &lt;&lt; ADEN);<br />\r\n<span style="color:#0000CD">&nbsp; // povolenie prevodu</span><br />\r\n&nbsp; ADCSRA |= (1 &lt;&lt; ADIF);<br />\r\n&nbsp; <span style="color:#0000CD">// pocka pokial neukonci prevod</span><br />\r\n&nbsp; while (!(ADCSRA &amp; (1 &lt;&lt; ADIF)));<br />\r\n&nbsp; <span style="color:#0000CD">// vysledok</span><br />\r\n&nbsp; return ADC;<br />\r\n}<br />\r\n<span style="color:#0000CD">/***<br />\r\n&nbsp;* Zobrazenie na dispej<br />\r\n&nbsp;*<br />\r\n&nbsp;* @param void<br />\r\n&nbsp;* @return void<br />\r\n&nbsp;*/</span><br />\r\nvoid lcd_show(void)<br />\r\n{<br />\r\n&nbsp; int voltage;<br />\r\n&nbsp; uint16_t adc;<br />\r\n&nbsp; char duty[3];<br />\r\n&nbsp; adc = adc_read(0); &nbsp;<br />\r\n&nbsp; <span style="color:#0000CD">// vypocet napatia v mV &nbsp;</span><br />\r\n&nbsp; voltage = (adc * 4.88);<br />\r\n&nbsp; <span style="color:#0000CD">// strieda</span><br />\r\n&nbsp; sprintf(duty, "%d", OCR1A);;<br />\r\n&nbsp; <span style="color:#0000CD">// ak je strieda mensia ako 100</span><br />\r\n&nbsp; if (OCR1A &lt; 100 &amp;&amp; OCR1A &gt;= 10) {<br />\r\n&nbsp; &nbsp; sprintf(duty, "0%d", OCR1A);<br />\r\n&nbsp; <span style="color:#0000CD">// ak je strieda mensua ako 10</span><br />\r\n&nbsp; } else if (OCR1A &lt; 10) {<br />\r\n&nbsp; &nbsp; sprintf(duty, "00%d", OCR1A);<br />\r\n&nbsp; }<br />\r\n&nbsp;&nbsp;<span style="color:#0000CD">// zapis do retazca</span><br />\r\n&nbsp; sprintf(to_lcd," PWM:%s/255\\nADC:%d %dmV", duty, adc, voltage);<br />\r\n&nbsp; <span style="color:#0000FF">// vymzanie displeja</span><br />\r\n&nbsp; lcd_clrscr();<br />\r\n&nbsp; <span style="color:#0000CD">// zapis textu na lcd</span><br />\r\n&nbsp; lcd_puts(to_lcd);<br />\r\n&nbsp; <span style="color:#0000CD">// prerusenie 0,5s</span><br />\r\n&nbsp; _delay_ms(500);<br />\r\n}</span></span></p>\r\n\r\n<h4>Prekladač</h4>\r\n\r\n<p><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">avr-gcc -Wall -Os -mmcu=atmega8 -c lib/lcd.c lib/pwm.c main.c</span></span><br />\r\n<span style="font-family:courier new,courier,monospace"><span style="font-size:12px">avr-gcc -Wall -mmcu=atmega8 lib/lcd.o lib/pwm.o main.o -o main</span></span><br />\r\n<span style="font-family:courier new,courier,monospace"><span style="font-size:12px">avr-objcopy -O ihex -R .eeprom main main.hex</span></span><br />\r\n<span style="font-size:12px"><span style="font-family:courier new,courier,monospace">avrdude -P /dev/ttyACM0 -b 19200 -c usbasp&nbsp;-p m8 -u -U flash:w:main.hex:i</span></span></p>\r\n\r\n<h4>Odporúčané odkazy</h4>\r\n\r\n<p><a href="http://svetelektro.com/clanky/programujeme-avr-v-jazyku-c-5-cast-456.html" target="_blank"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><em>http://svetelektro.com/clanky/programujeme-avr-v-jazyku-c-5-cast-456.html</em></span></span></a><br />\r\n<a href="http://www.atmel.com/images/atmel-2486-8-bit-avr-microcontroller-atmega8_l_datasheet.pdf" target="_blank"><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif"><em>http://www.atmel.com/images/atmel-2486-8-bit-avr-microcontroller-atmega8_l_datasheet.pdf</em></span></span></a></p>\r\n', '2016-03-20 19:08:16', NULL, 'released'),
(19, 1, 'Zdroj', 'zdroj', 'Diel 1 - pomocné obvody', 'diel-1-pomocne-obvody', '<h3>Pomocné napájanie</h3>\r\n\r\n<p style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">Pri konštrukcii regulovateľného zdroja napätia sa nezaobídeme bez napájania pomocných obvodov ako sú&nbsp;operačné zosilňovače, displeje, mikroprocesory, ktoré ku svojej činnosti potrebujú rôzne hodnoty napätia ako napríklad +5V, +15V a -15V.</span></span></p>\r\n\r\n<h4>Schéma zapojenia</h4>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Schéma zapojenia pomocného napájania&nbsp;je uvedená na Obr.1. Doska plošného spoja je uvedená na Obr.2., a rozloženie súčiastok na&nbsp;Obr.3.</span></span></p>\r\n\r\n<p><a href="/Public/images/zdroj/Napajanie_5V_schema.png" target="_blank"><img alt="" src="/Public/images/zdroj/Napajanie_5V_schema.png" style="height:161px; width:500px" /></a><br />\r\n<strong><span style="font-size:10px"><span style="font-family:arial,helvetica,sans-serif">Obr. 1 - Schéma zapojenia pomocného zdroja s napätím +5V a -5V</span></span></strong></p>\r\n\r\n<h4>Návrh plošného spoja</h4>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Návrh schémy plošného spoja pomocného napájania bola vytvorená v programe Eagle.&nbsp;</span></span></p>\r\n\r\n<p><a href="/Public/images/zdroj/Napajanie_5V_navrh.png" target="_blank"><img alt="Návrh dosky plošného spoja" src="/Public/images/zdroj/Napajanie_5V_navrh.png" style="height:313px; width:500px" /></a><br />\r\n<span style="font-size:10px"><span style="font-family:arial,helvetica,sans-serif"><strong>Obr. 2 - Návrh dosky plošného spoja</strong></span></span></p>\r\n\r\n<h4>Schéma rozloženia súčiastok</h4>\r\n\r\n<p><a href="/Public/images/zdroj/Napajanie_5V_rozlozenie.png" target="_blank"><img alt="" src="/Public/images/zdroj/Napajanie_5V_rozlozenie.png" style="height:313px; width:500px" /></a><br />\r\n<strong><span style="font-size:10px"><span style="font-family:arial,helvetica,sans-serif">Obr. 4&nbsp;- Návrh dosky plošného spoja s rozložením súčiastok</span></span></strong></p>\r\n\r\n<h4>Realizácia - tvorba plošného spoja</h4>\r\n\r\n<p style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">Na vytvorenie dosky plošného spoja som sa rozhodol použiť&nbsp;nažehľovaciu&nbsp;metóda. Vyžaduje si&nbsp;nižšiu&nbsp;náročnosť a&nbsp;keďže&nbsp;v tomto prípade nie je potrebná vysoká presnosť, je nadmerne postačujúca. Čo sa týka leptania, to bolo&nbsp;realizované&nbsp;v chloride železitom.</span></span></p>\r\n\r\n<p><a href="/Public/images/zdroj/Napajanie_5V_nazehlenie.png" style="line-height: 20.8px;" target="_blank"><img alt="" src="/Public/images/zdroj/Napajanie_5V_nazehlenie.png" style="height:292px; width:500px" /></a><br />\r\n<strong><span style="font-size:10px"><span style="font-family:arial,helvetica,sans-serif">Obr. 5&nbsp;- Návrh dosky plošného spoja nažehľovacou metódou</span></span></strong><br />\r\n<br />\r\n<a href="/Public/images/zdroj/Napajanie_5V_vyleptanie.png" style="line-height: 20.8px;" target="_blank"><img alt="" src="/Public/images/zdroj/Napajanie_5V_vyleptanie.png" style="height:272px; width:500px" /></a><br />\r\n<strong><span style="font-size:10px"><span style="font-family:arial,helvetica,sans-serif">Obr. 6&nbsp;- Návrh dosky plošného spoja po vyleptaní</span></span></strong></p>\r\n\r\n<h4>Osadenie súčiastok plošného spoja</h4>\r\n\r\n<p><a href="/Public/images/zdroj/Napajanie_5V_osadenie_2.png" target="_blank"><img alt="" src="/Public/images/zdroj/Napajanie_5V_osadenie_2.png" style="height:334px; width:500px" /></a><br />\r\n<strong><span style="font-size:10px"><span style="font-family:arial,helvetica,sans-serif">Obr. 7&nbsp;-&nbsp;Doska&nbsp;plošného spoja po zaspájkovaní</span></span></strong><br />\r\n<br />\r\n<a href="/Public/images/zdroj/Napajanie_5V_osadenie_1.png" target="_blank"><img alt="" src="/Public/images/zdroj/Napajanie_5V_osadenie_1.png" style="height:655px; width:500px" /></a><br />\r\n<strong><span style="font-size:10px"><span style="font-family:arial,helvetica,sans-serif">Obr. 8&nbsp;- Doska&nbsp;plošného spoja po osadení súčiastok a spájkovani</span></span></strong></p>\r\n', '2016-04-18 20:35:47', NULL, 'released'),
(20, 1, 'Ubuntu', 'ubuntu', 'Diel 3 - Phpmyadmin', 'diel-3-phpmyadmin', '<h3>PhpMyAdmin</h3>\r\n\r\n<p style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">Aktualiz&aacute;ciami v Ubuntu do&scaron;lo k odstr&aacute;neniu odkazu phpmyadmin z adresy&nbsp;localhostu (http://localhost/phpmyadmin).</span></span></p>\r\n\r\n<h4 style="text-align:justify">Rie&scaron;enie</h4>\r\n\r\n<p style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">Rie&scaron;en&iacute;m je vytvorenie&nbsp;symbolick&eacute;ho odkazu na phpmyadmin z priečinka /usr/share/phpmyadmin</span></span></p>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:11px"><span style="font-family:courier new,courier,monospace">sudo ln -s /usr/share/phpmyadmin /var/www/html/phpmyadmin</span></span></div>\r\n\r\n<h4>Odkazy</h4>\r\n\r\n<p><span style="font-size:12px"><em><span style="font-family:courier new,courier,monospace"><a href="http://askubuntu.com/questions/168191/where-is-my-phpmyadmin-installation" style="color: rgb(0, 0, 0); text-decoration: none;" target="_blank">http://askubuntu.com/questions/168191/where-is-my-phpmyadmin-installation</a></span></em></span></p>\r\n', '2016-04-28 19:56:02', NULL, 'draft'),
(22, 1, 'Ubuntu', 'ubuntu', 'Diel 4 - USBAsp', 'diel-4-usbasp', '<h3>Avrdude v&yacute;straha</h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Avrdude warning:&nbsp;cannot query manufacturer for device: error sending control message: Operation not permitted</span></span></p>\r\n\r\n<h4 style="text-align:justify">Rie&scaron;enie</h4>\r\n\r\n<div style="background:#eee;border:1px solid #ccc;padding:5px 10px;"><span style="font-size:11px"><span style="font-family:courier new,courier,monospace"><code>SUBSYSTEMS==&quot;usb&quot;, ATTRS{idVendor}==&quot;16c0&quot;, ATTRS{idProduct}==&quot;05dc&quot;, GROUP=&quot;users&quot;, MODE=&quot;0666&quot;</code></span></span></div>\r\n\r\n<h4>Odkazy</h4>\r\n\r\n<p><em><span style="font-size:12px"><span style="font-family:courier new,courier,monospace"><a href="https://wiki.archlinux.org/index.php/Udev#Accessing_Firmware_Programmers_and_USB_Virtual_Comm_Devices" style="color: rgb(0, 0, 0); text-decoration: none;" target="_blank">https://wiki.archlinux.org/index.php/Udev#Accessing_Firmware_Programmers_and_USB_Virtual_Comm_Devices</a></span></span></em></p>\r\n', '2016-06-06 19:11:08', NULL, 'draft'),
(23, 1, 'Zdroj', 'zdroj', 'Diel 2 - návrh filtra PWM', 'diel-2-navrh-filtra-pwm', '<h4>Návrh dolnopriepustného filtra PWM</h4>\r\n\r\n<p><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Súpis základných informácii o PWM signále na výstupe mikrokontroléra:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">zvolený mod 2 - 9bitová&nbsp;rozlíšovacia schopnosť</span></span>\r\n\r\n	<ul>\r\n		<li><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">inkrementácia od BOTTOM = 0 po TOP = 0x01FF (512 hodnôt)</span></span></li>\r\n	</ul>\r\n	</li>\r\n	<li><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">frekvencia PWM</span></span>\r\n	<ul>\r\n		<li><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">frekvencia pri predeličke N = 1 =&gt; f = 8000000/(1.(1+511)) = 15625 Hz</span></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<h4><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif">Navrhovaný typ PWM filtra</span></span></h4>\r\n\r\n<p><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">Topológia Sallen - key s Butterworth podmienkami maximalnej rovnosti v prenášanom pásme.</span></span></p>\r\n', '2016-06-13 08:37:34', NULL, 'draft');
INSERT INTO `Articles` (`Id`, `Id_Users`, `Category`, `Category_unaccent`, `Title`, `Title_unaccent`, `Content`, `Registered`, `Edited`, `Type`) VALUES
(24, 1, 'Atmega16', 'atmega16', 'Diel 1 - teória AD prevodníka', 'diel-1-teoria-ad-prevodnika', '<h3>Analogovo digitálny prevodník Atmega16</h3>\r\n\r\n<ul>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">10 bitová rozlišovacia schopnosť (0 - 1023 hodnôt)</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">0,5 LSB integrálna nelinearita</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">+/- 2 LSB absolútna presnosť</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">13 - 260 us doba prevodu</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">až 15 kSPS pri maximálnom rozlíšení</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">8 multiplexových vstupov</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">7 diferenciálnych vstupov</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">2 diferenciálne vstupy so zosilnením 10x a 200x</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">možnosť zarovnania výsledku</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">voliteľné interné referenčné napätie 2,56V</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">free running alebo jednoprevodový mód</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">štart prevodu možný autospúšťaním na prerušenie rôznych zdrojov</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif"><span style="font-size:12px">prerušenie po ukončení prevodu</span></span></li>\r\n</ul>\r\n\r\n<h4>ADMUX - ADC&nbsp;multipexer register</h4>\r\n\r\n<table border="1" cellpadding="10" cellspacing="1">\r\n	<thead>\r\n		<tr>\r\n			<th scope="row" style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">REFS1</span></span></th>\r\n			<th scope="col" style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">REFS2</span></span></th>\r\n			<th scope="col" style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">ADLAR</span></span></th>\r\n			<th scope="col" style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">MUX4</span></span></th>\r\n			<th scope="col" style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">MUX3</span></span></th>\r\n			<th scope="col" style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">MUX2</span></span></th>\r\n			<th scope="col" style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">MUX1</span></span></th>\r\n			<th scope="col" style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">MUX0</span></span></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style="border-color:rgb(0, 0, 0); text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">R/W</span></span></td>\r\n			<td style="border-color:rgb(0, 0, 0); text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">R/W</span></span></td>\r\n			<td style="border-color:rgb(0, 0, 0); text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">R/W</span></span></td>\r\n			<td style="border-color:rgb(0, 0, 0); text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">R/W</span></span></td>\r\n			<td style="border-color:rgb(0, 0, 0); text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">R/W</span></span></td>\r\n			<td style="border-color:rgb(0, 0, 0); text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">R/W</span></span></td>\r\n			<td style="border-color:rgb(0, 0, 0); text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">R/W</span></span></td>\r\n			<td style="border-color:rgb(0, 0, 0); text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">R/W</span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<ul>\r\n	<li style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>REFS1:REFS0</strong> - výber referenčného napätia pre AD prevodník. Pokiaľ je pripojené napätie na AREF nesmie sa aktivovať interné napätie.&nbsp;</span></span></li>\r\n</ul>\r\n\r\n<table border="1" cellpadding="10" cellspacing="1" style="margin-left:25px">\r\n	<caption><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Referenčné napätie REFS1:0</span></span></caption>\r\n	<thead>\r\n		<tr>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px"><strong>REFS1</strong></span></span></th>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px"><strong>REFS0</strong></span></span></th>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px"><strong>Výber referenčného napätia</strong></span></span></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">AREF, Interné referenčné napätie deaktivované</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">1</span></span></td>\r\n			<td><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">AVCC s externým kondenzátorom na AREF pine</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">1</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">Rezervované</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">1</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">1</span></span></td>\r\n			<td><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">Interné 2,56V s externým kondenzátorom na AREF pine</span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<ul>\r\n	<li style="text-align:justify"><span style="font-size:12px"><span style="font-family:verdana,geneva,sans-serif"><strong>ADLAR</strong> -&nbsp;nastavuje zarovnanie výsledku prevodu, ADLAR = 1 zarovnanie smerom hore (v ADCH 8 bitov), ADLAR = 0 zarovnanie smerom dole (v ADCL 8 bitov)</span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:verdana,geneva,sans-serif; font-size:12px"><strong>MUX4:MUX0</strong> - výberá kanál&nbsp;AD prevodníka, t.j. jedného z 8 vstupov (pinov PC0 - PC7) na porte C, pričom umožňuje zosilňovať diferenciálne napätie&nbsp;</span></li>\r\n</ul>\r\n\r\n<table border="1" cellpadding="10" cellspacing="1" style="margin-left:25px">\r\n	<caption><span style="font-size:12px"><span style="font-family:arial,helvetica,sans-serif">Výberá kanál AD prevodníka</span></span></caption>\r\n	<thead>\r\n		<tr>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">MUX4</span></span></th>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px"><strong>MUX3</strong></span></span></th>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px"><strong>MUX2</strong></span></span></th>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px"><strong>MUX1</strong></span></span></th>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px"><strong>MUX0</strong></span></span></th>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">Vstup</span></span></th>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">Neinv. vstup</span></span></th>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">Inv. vstup</span></span></th>\r\n			<th scope="col"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">Zisk</span></span></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">ADC0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">N/A</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">N/A</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">N/A</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">1</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">ADC1</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">N/A</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">N/A</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">N/A</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">1</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">0</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">ADC2</span></span></td>\r\n			<td style="text-align:center"><span style="font-family:courier new,courier,monospace"><span style="font-size:12px">N/A</span></span></td>\r\n			<td style="text-align:center"><span style="fon